import React from "react";
import axios from "axios";
import { useState } from 'react';


function Login(){
    
    


const initialValueOne = {
    name: '',
    senha: '',
    cargo: '',  
  }

 
  const [values, setValues] = useState(initialValueOne);

  function onChangeOne(ev) {
    const { name, value } = ev.target;
    setValues({ ...values, [name]: value });
  }

  function onSubmitOne(ev) {
    ev.preventDefualt()
    return loginFunction;
  }

  const handleSubmit = (event) => {
    event.preventDefualt();

    console.log("submit")
  }
   
        async function loginFunction() {
        const { data: { token } } = await axios.post('http://localhost:3333/authenticate', values);
        localStorage.setItem('token', JSON.stringify(token));
        console.log(token)
        axios.defaults.headers.Authorization = `Bearer ${token}`; 
        return('/torcedores')
};

return( 
    <div className="CreateInputName">
    <form className="FormuEdit" onSubmit={onSubmitOne}>
       <div className='EditInput'>
         <div id='EditInputName'>
           <label>Name:</label><br />
           <input
             type="text" id="name" name="name" onChange={onChangeOne}>
           </input>
         </div>
         <div id='EditInputSexo'>
           <label>Senha:</label><br />
           <input
              id="sexo" name="senha" type="text" onChange={onChangeOne}></input>
         </div>
         <div id='EditInputSexo'>
           <label>Cargo:</label><br />
           <input
             list='cargos' id="idade" name="sexo" type="text" onChange={onChangeOne}></input>
             <datalist id='cargos'>
             <option value='Administrador'/>
             <option value='Diretor'/>
             <option value='Vendendor'/>
           </datalist>
         </div>
       </div>
       <button className='buttonEditModal'  onSubmit={onSubmitOne} >Logar</button>
     </form>
   </div> 
);
}

export default Login;
