import React, { useContext } from 'react';
import { Switch, Route, Redirect } from 'react-router-dom';

import { Context } from './Context/AuthContext';

import Login from './pages/Login';
import Users from './pages/Users';

function CustomRoute({ isPrivate, ...rest }) {
  const { loading, authenticated } = useContext(Context); // chama a page de autenticação

  if (loading) {
    return <h1>Loading...</h1>; // se tiver carregando chama h1
  }

  if (isPrivate && !authenticated) {
    return <Redirect to="/login" /> // se for diferente de login desloga e retorna pra page web Login
  }

  return <Route {...rest} />; 
}

export default function Routes() {
  return (
    <Switch>
      <CustomRoute exact path="/loginaa" component={Login} /> 
      <CustomRoute isPrivate exact path="/users" component={Users} />
    </Switch>
  );
}
