import './App.css';
import { useEffect, useState } from 'react';
import axios from "axios";
import 'bootstrap/dist/css/bootstrap.min.css';
import {Table} from "reactstrap";
import Modal from 'react-modal';



function App() {

  const Torcedores = ({torcedores}) => {
    return (


////////////////////////////////////////////////table start///////////////////////////////////////////////////////   

      <div className='divTable'>



        <Table hover  borderless responsive className='teste'>
          <thead> 
            <tr>
             <th style={{"borderBottomWidth":"1px","borderBottomRadius":"10px" , 'borderBottomColor':"#666666","borderRightWidth":"1px","borderRightRadius":"10px" , 'borderRightColor':"#666666", 'borderStyle':'solid'}}>
              Nome
             </th>
              <th style={{"borderBottomWidth":"1px","borderBottomRadius":"10px" , 'borderBottomColor':"#666666","borderRightWidth":"1px","borderRightRadius":"10px" , 'borderRightColor':"#666666", 'borderStyle':'solid'}}>
                Idade
              </th>
              <th style={{"borderBottomWidth":"1px","borderBottomRadius":"10px" , 'borderBottomColor':"#666666","borderRightWidth":"1px","borderRightRadius":"10px" , 'borderRightColor':"#666666", 'borderStyle':'solid'}}>
                Sexo
              </th>
              <th style={{"borderBottomWidth":"1px","borderBottomRadius":"10px" , 'borderBottomColor':"#666666","borderRightWidth":"1px","borderRightRadius":"10px" , 'borderRightColor':"#666666", 'borderStyle':'solid'}}>
                Sócio 
              </th>
              <th style={{"borderBottomWidth":"1px","borderBottomRadius":"10px" , 'borderBottomColor':"#666666", 'borderStyle':'solid'}}>
                  Actions
              </th>
            </tr>
          </thead>
          <tbody>
          {torcedores.map((torcedor,key) => {
            return ( <tr key={key}>
              <td style={{"borderRightWidth":"1px","borderRightRadius":"10px" , 'borderRightColor':"#666666", 'borderStyle':'solid'}}>
                {torcedor.name}
              </td>
              <td  style={{"borderRightWidth":"1px","borderRightRadius":"10px" , 'borderRightColor':"#666666", 'borderStyle':'solid'}}>
                {torcedor.idade}
              </td>
              <td style={{"borderRightWidth":"1px","borderRightRadius":"10px" , 'borderRightColor':"#666666", 'borderStyle':'solid'}}>
                {torcedor.sexo}
              </td>
              <td style={{"borderRightWidth":"1px","borderRightRadius":"10px" , 'borderRightColor':"#666666", 'borderStyle':'solid'}}>
              {torcedor.socio_torcedor ? 
                <button className= 'Positivo'> Positivo </button> : 
                <button className= 'Negativo'> Negativo </button>}
                </td>
                <td >
                <button className= 'Negativo'onClick={() => deleteTorcedor(torcedor)}> Delete </button>
                <button id="Edit" className= 'modal-button'onClick={() => handelEditTorcedor(torcedor)}> Edit </button>
                </td>

                </tr>)
              })}
          </tbody>
  </Table>

           </div>
       )
     }

/////////////////////////////////////////////////table end///////////////////////////////////////////////////////////

 ////////////////////////////////////////////////modal create start//////////////////////////////////////////////////
  Modal.setAppElement('#root')

  const [modalIsOpen, setIsOpen] = useState(false)  

  function handleOpenModal() {
    setIsOpen(true)
  }
  function handleCloseModal() {
    setIsOpen(false)
  }
   const customStyles = {
    content: {
      top: '50%',
      left: '50%',
      right: 'auto', 
      bottom: 'auto',
      marginRigth: '-50%',
      transform: 'translate(-50%, -50%)',
    }
  }

  const initialValue = {
   name: '',
   cpf: '',
   idade: 0,
   sexo: '',
   socio_torcedor: false,
   faixa_etaria: '', 
  }

   const [values, setValues] = useState(initialValue);


   function onChange(ev) {
     const { name, value } = ev.target;

     setValues({ ...values, [name]: value });
   }

   function onSubmit(ev) {
     console.log(values)
     values.idade = parseInt(values.idade)
     axios.post("http://localhost:3333/torcedores",values)
    ev.preventDefault();
  }
////////////////////////////////modal create end//////////////////////////////////////////////


 ///////////////////////////////modal edit start//////////////////////////////////////////////
  const [modalIsOpenEdit, setIsOpenEdit] = useState(false)

  function handleOpenModalEdit() {
    setIsOpenEdit(true)
  }

  function handleCloseModalEdit() {
    setIsOpenEdit(false)
  }

  const customStylesEdit = {
    content: {
      top: '50%',
      left: '50%',
      right: 'auto', 
      bottom: 'auto',
      marginRigth: '-50%',
      transform: 'translate(-50%, -50%)',
    }
  }
 ////////////////////////////////modal edit end///////////////////////////////////////////////////////////


 ///////////////////////////////function start////////////////////////////////////////////////////////////
  async function  getTorcedores(){
    console.log("eu")
    const response = await axios.get("http://localhost:3333/torcedores")
    setTorcedores(response.data);

  };

  async function setNumberOfPartnerFans() { 
    console.log (torcedores)
    const partnerFans = torcedores.filter((torcedor) => {
      return torcedor.socio_torcedor === true
    })
    setNumberOfPartner(partnerFans.length ) 
  }

  async function setMasculineFans() {
    const masculineFans = torcedores.filter((torcedor) => {
      return torcedor.sexo === "Masculino"
    })
    setNumberOfMasculineFans(masculineFans.length)

  } 

  async function setWomenFans() {
    const womenFans = torcedores.filter((torcedor) => {
      return torcedor.sexo === "Feminino"
    })
    setNumberOfWomenfans(womenFans.length)
  }

  async function setChildFans() {
    const ChildFans = torcedores.filter((torcedor) => {
    return torcedor.faixa_etaria === "Criança"
  })
  setNumberOfChildsFans(ChildFans.length)
}

  async function setTeenagerFans() {
    const tennagerFans = torcedores.filter((torcedor) => {
      return torcedor.faixa_etaria === "Adolecente"
    })
    setNumberOfTennager(tennagerFans.length)
  }

  async function setAdultsFans() {
    const adultsFans = torcedores.filter((torcedor) => {
      return torcedor.faixa_etaria === "Adulto"
    })
    setNumberOfAdults(adultsFans.length)
  }

  async function setLgbtFans() {
    const lgbtFans = torcedores.filter((torcedor) => {
      return torcedor.sexo === "LGBTQIA+"
    })
    setNumberOfLgbt(lgbtFans.length)
  }

  async function handelEditTorcedor(torcedor){
    setSelectedTorcedor(torcedor);
    handleOpenModalEdit(true)
  }

  async function editTorcedor(){
    const response = await axios.put("http://localhost:3333/torcedores",{
     id: selectedTorcedor.id,
     name: inputValue,
     idade: inputValueIdade,
     sexo: inputValueSexo,
     socio_torcedor: inputValueSocioTorcedor,
  })
    setSelectedTorcedor();
    setInputVisibility(false);
    getTorcedores();
    setInputValue("");
    setInputValueIdade("");
    setInputValueSexo("");
    setInputValueSocioTorcedor("");
  };

  async function deleteTorcedor(torcedor) {
    console.log(torcedor)
    const response = await axios.delete(`http://localhost:3333/torcedores/${torcedor.id}`);
    getTorcedores();
  } 

  async function modifyStatusTorcedor(torcedor) {
    const response = await axios.put("http://localhost:3333/torcedores", {
      id: torcedor.id,
      status: !torcedor.status
    });
  }

  async function createTorcedor() {
    const response = await axios.post("http://localhost:3333/torcedores", {name: inputValue,cpf:"058.956.803-56",})
    getTorcedores();
    setInputVisibility(!inputVisibility);
    setInputValue("");
  };

  //////////////////////////////////////function end/////////////////////////////////////////////////////////

////////////////////////////////////////const start///////////////////////////////////////////////////////////
  const [torcedores, setTorcedores] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [inputValueName, setInputValueName] = useState("");
  const [inputValueIdade, setInputValueIdade] = useState();
  const [inputValueSexo, setInputValueSexo] = useState("");
  const [inputValueSocioTorcedor, setInputValueSocioTorcedor] = useState("");
  const [inputVisibility, setInputVisibility] = useState(false);
  const [selectedTorcedor, setSelectedTorcedor] = useState();
  const [numberOfPartner, setNumberOfPartner] = useState(0);
  const [numberOfMasculineFans, setNumberOfMasculineFans] = useState(0);
  const [numberOfWomenFans, setNumberOfWomenfans] = useState(0);
  const [numberOfChildFans, setNumberOfChildsFans] = useState(0);
  const [numberOfTennager, setNumberOfTennager] = useState(0);
  const [numberOfAdults, setNumberOfAdults] = useState(0);
  const [numberOfLgbt, setNumberOfLgbt] = useState(0);
  useEffect(() => {
    getTorcedores();

  },[])
  useEffect(() => {
    setNumberOfPartnerFans();
    setMasculineFans();
    setWomenFans();
    setAdultsFans();
    setChildFans();
    setLgbtFans();
    setTeenagerFans(); 
  },[torcedores])

//////////////////////////////////////////////const end///////////////////////////////////////////////////////


/////////////////////////////////////////////Html start//////////////////////////////////////////////////////


  return (
    <div className="App">
      <header className="container">

        <div className="header">
          <h1>Saa lets Go</h1>
        </div>

        <Torcedores torcedores={torcedores}></Torcedores>
        <div className='sideTable'>



          {/* ModalCreate */}

          <button className= 'modal-button' onClick={handleOpenModal}> + Adicionar Torcedor </button>

          <Modal isOpen={modalIsOpen}
          onRequestClose={handleCloseModal} style={customStyles} >
       <div className="App">
       <h1> alow </h1>
       <h2> torcedor </h2>

        <form onSubmit={onSubmit} className="Formu">

          <div className="promotion-form__group">
            <label htmlFor="title">name</label>
            <input id="name" name="name" type="text" onChange={onChange} />
          </div>

          <div className="promotion-form__group">
            <label htmlFor="title">cpf</label>
            <input id="cpf" name="cpf" type="text" onChange={onChange} />
          </div>

          <div className="promotion-form__group">
            <label htmlFor="title">idade</label>
            <input id="idade" name="idade" type="number" onChange={onChange} />
          </div>

          <div className="promotion-form__group">
            <label htmlFor="title">sexo</label>
            <input id="sexo" name="sexo" type="text" onChange={onChange} />
          </div>

          <div className="promotion-form__group">
            <label htmlFor="title">socio_torcedor</label>
            <input id="socio_torcedor" name="socio_torcedor" type="checkbox" onChange={onChange} />
          </div>

          <div>
            <button type="submit">Salvar</button>
          </div>


      </form>


    </div>

</Modal>



{/* modalEdit */}


<Modal isOpen={modalIsOpenEdit}
          onRequestCloseEdit={handleCloseModalEdit} style={customStylesEdit} >
       <div className="App">
       <h1> alo </h1>
       <h2> torcedor </h2>


       <form  className="Formu">
        {/* <form onSubmitEdit={onSubmitEdit} className="Formu"> */}

          {/* <div className="promotion-form__group">
            <label htmlFor="title">name</label>
            <input id="name" name="name" type="text" onChangeEdit={onChangeEdit} />
          </div>

          <div className="promotion-form__group">
            <label htmlFor="title">cpf</label>
            <input id="cpf" name="cpf" type="text" onChangeEdit={onChangeEdit} />
          </div>

          <div className="promotion-form__group">
            <label htmlFor="title">idade</label>
            <input id="idade" name="idade" type="number" onChangeEdit={onChangeEdit} />
          </div>

          <div className="promotion-form__group">
            <label htmlFor="title">sexo</label>
            <input id="sexo" name="sexo" type="text" onChangeEdit={onChangeEdit} />
          </div>

          <div className="promotion-form__group">
            <label htmlFor="title">socio_torcedor</label>
            <input id="socio_torcedor" name="socio_torcedor" type="checkbox" onChangeEdit={onChangeEdit} />
          </div> */}

          <div>
            <button onClick={selectedTorcedor ? editTorcedor : handelEditTorcedor}>Salvar</button>

             <input
              value={inputValue} onChange={(event) => { setInputValue(event.target.value);}}>
             </input>


             <input 
              value={inputValueIdade} onChange={(event) => { setInputValueIdade(event.target.value);}}>
             </input>

             <input 
              value={inputValueSexo} onChange={(event) => { setInputValueSexo(event.target.value);}}>
             </input>

             <input 
              value={inputValueSocioTorcedor} onChange={(event) => { setInputValueSocioTorcedor(event.target.value);}}>
             </input>

          </div>




      </form>


    </div>

</Modal>


{/* relatorios */}

            <div className='relatorios'>     
              <div className='relatorio'>
                  <p className='titleRelatorio'>Socio Torcedor</p>
                  <h3 className='valueRelatorio'>{numberOfPartner}</h3>
                </div>
                <div className='relatorio'>
                  <p className='titleRelatorio'>Torcedores Masculinos</p>
                  <h3 className='valueRelatorio'>{numberOfMasculineFans}</h3>
                </div>
                <div className='relatorio'>
                  <p className='titleRelatorio'>Torcedores Femininos</p>
                  <h3 className='valueRelatorio'>{numberOfWomenFans}</h3>
                </div>
                <div className='relatorio'>
                  <p className='titleRelatorio'>Torcedores Crianças</p>
                  <h3 className='valueRelatorio'>{numberOfChildFans}</h3>
                </div>
                <div className='relatorio'>
                  <p className='titleRelatorio'>Torcedores Adolecentes</p>
                  <h3 className='valueRelatorio'>{numberOfTennager}</h3>
                </div>
                <div className='relatorio'>
                  <p className='titleRelatorio'>Torcedores Adultos</p>
                  <h3 className='valueRelatorio'>{numberOfAdults}</h3>
                </div>
                <div className='relatorio'>
                  <p className='titleRelatorio'>Torcedores LGBTQIA+</p>
                  <h3 className='valueRelatorio'>{numberOfLgbt}</h3>
                </div>

            </div>
        </div>


      </header>

    </div>
  );


}
export default App;
