import React from "react";
import axios from "axios";
import { useState } from 'react';

function Login(){
    
    return( 
        <div className="CreateInputName">
        <form className="FormuEdit">
           <div className='EditInput'>
             <div id='EditInputName'>
               <label>Name:</label><br />
               <input
                 type="text" id="name" name="name" onChange={onChange}>
               </input>
             </div>
             <div id='EditInputSexo'>
               <label>Senha:</label><br />
               <input
                  id="sexo" name="senha" type="text" onChange={onChange}></input>
             </div>
             <div id='EditInputSexo'>
               <label>Cargo:</label><br />
               <input
                 list='cargos' id="idade" name="sexo" type="text" onChange={onChange}></input>
                 <datalist id='cargos'>
                 <option value='Administrador'/>
                 <option value='Diretor'/>
                 <option value='Vendendor'/>
               </datalist>
             </div>
           </div>
           <button className='buttonEditModal'  onSubmit={onSubmit} >Logar</button>
         </form>
       </div> 
    );
}

const [values, setValues] = useState(initialValue);

const initialValue = {
    name: '',
    senha: '',
    cargo: '',  
  }

 

  function onChange(ev) {
    const { name, value } = ev.target;
    setValues({ ...values, [name]: value });
  }

  function onSubmit(ev) {
    axios.post("http://localhost:3333/authenticate", values)
    return loginFunction;
  }
   
        async function loginFunction() {
        const { data: { token } } = await axios.post('http://localhost:3333/authenticate');
        localStorage.setItem('token', JSON.stringify(token));
        axios.defaults.headers.Authorization = `Bearer ${token}`;
        setAuthenticated(true) 
        return 'http://localhost:3333/torcedores'
};
export default Login;
