import React from "react";
import axios from "axios";
import { useEffect, useState } from 'react';

function Login(){
    
    return( 
        <div className="CreateInputName">
        <form className="FormuEdit">
           <div className='EditInput'>
             <div id='EditInputName'>
               <label>Name:</label><br />
               <input
                 type="text">
               </input>
             </div>
             <div id='EditInputSexo'>
               <label>Senha:</label><br />
               <input
                 list='sexos'></input>
                 <datalist id='sexos'>
                 <option value='Masculino'/>
                 <option value='Feminino'/>
                 <option value='LGBTQIA+'/>
               </datalist>
             </div>
             <div id='EditInputSexo'>
               <label>Cargo:</label><br />
               <input
                 list='sexos'></input>
                 <datalist id='sexos'>
                 <option value='Masculino'/>
                 <option value='Feminino'/>
                 <option value='LGBTQIA+'/>
               </datalist>
             </div>
           </div>
           <button className='buttonEditModal'  onChange={onChange} >Logar</button>
         </form>
       </div> 
    );
}
    
    async function loginFunction() {
        const response = await axios.post("http://localhost:3333/authenticate", {
          name:"",
          senha:"",
          cargo:"",
        })
        const [values, setValues] = useState(response);
        const { data: { token } } = await axios.post('http://localhost:3333/authenticate');
        localStorage.setItem('token', JSON.stringify(token));
        axios.defaults.headers.Authorization = `Bearer ${token}`;
        setAuthenticated(true)  
        return 'http://localhost:3333/torcedores'
      
  
    function onChange(ev) {
      const { name, value } = ev.target;
      setValues({ ...values, [name]: value });
    }
    
};
export default Login;
