import {BrowserRouter ,Route, Routes, Navigate} from 'react-router-dom';
import React from 'react';
import history from './history';




import Login from './login/index';
import Torcida from './torcedores/index';

function App() {
  const token = localStorage.getItem('token');
  const PrivateRoute = ({ ...rest}) => (
    <Route
      {...rest}
      render={(props) =>
        token ? (
          <Navigate replace to="/torcedores" />
        ) : (
          <Navigate replace to="/" />
        )
      }
    />
  );
  function CustomRoute({ isPrivate, ...rest }) {
    const token = localStorage.getItem('token');
    if (isPrivate && !token) {
      return <Redirect to="/login" /> // se for diferente de login desloga e retorna pra page web Login
    }

    return <Route {...rest} />; 
  }
  return(

  <BrowserRouter>
            <Routes history={history}>
              <CustomRoute path="/" element={<Login/>} />
              <CustomRoute isPrivate path="/torcedores" element={<Torcida/>} />
                {/* <PrivateRoute path="/" element={<Login/>}> </PrivateRoute>   
                <Route path="/" element={<Login/>} /> 
                {token && <Route path="/torcedores" element={<Torcida/>} />} 
                <Route path="*" element={<Login/>} /> */}
            </Routes>
  </BrowserRouter>


          )

}
export default App;
