import React from "react";
import axios from "axios";
import { useState, useEffect } from 'react';




// function Login(){
//   let navigate = useNavigate();
//   useEffect(()=> {
//     const token = localStorage.getItem('token')
//     if (token) {
//       navigate('/torcedores')
//     }
//   },[]) 

    
 function useAuth() {
    const [authenticated, setAuthenticated] = useState(false); 

    useEffect(() => {
      const token = localStorage.getItem('token'); // pega o token
  
      if (token) {
        api.defaults.headers.Authorization = `Bearer ${JSON.parse(token)}`;
        setAuthenticated(true); // se passar pela a autorização authenticated vai ser true
      }
    },[]);


const initialValueOne = {
    name: '',
    senha: '',
    cargo: '',  
  }

 
  const [values, setValues] = useState(initialValueOne);
  
  
  function onChangeOne(ev) {
    const { name, value } = ev.target;
    setValues({ ...values, [name]: value });
  }

  function onSubmitOne(ev) {
    ev.preventDefault()
   loginFunction();
  }

  const handleSubmit = (event) => {
    event.preventDefault();

    console.log("submit")
  }
   
        async function loginFunction() {
        const { data: { token } } = await axios.post('http://localhost:3333/authenticate', values);
        localStorage.setItem('token', JSON.stringify(token));
        console.log(token)
        axios.defaults.headers.Authorization = `Bearer ${token}`;
        setAuthenticated(true); 
        navigate('/torcedores')
        // if(token == "diretor"){("buttonEditModal").disabled = true}
        // if(token == "vendendor"){("buttonEditModal").disabled = true}
        // if(token == "vendendor"){("buttonCreateModal").disabled = true}
       
};

function handleLogout() {
  setAuthenticated(false);
  localStorage.removeItem('token'); // quando sair o token vai deslogar
  axios.defaults.headers.Authorization = undefined;
  navigate('/login'); // quando deslogar vai para pagWeb do login
}

return( 
    <div className="CreateInputName">
    <form className="FormuEdit" onSubmit={onSubmitOne}>
       <div className='EditInput'>
         <div id='EditInputName'>
           <label>Name:</label><br />
           <input
             type="text" id="name" name="name" required="name" onChange={onChangeOne}>
           </input>
         </div>
         <div id='EditInputSexo'>
           <label>Senha:</label><br />
           <input
              id="sexo" name="senha" type="text" required="senha"  onChange={onChangeOne}></input>
         </div>
         <div id='EditInputSexo'>
           <label>Cargo:</label><br />
           <input
             list='cargos' id="idade" name="sexo" required="cargos" type="text" onChange={onChangeOne}></input>
             <datalist id='cargos'>
             <option value='Administrador'/>
             <option value='Diretor'/>
             <option value='Vendendor'/>
           </datalist>
         </div>
       </div>
       <button className='buttonEditModal' onSubmit={ (event)=> {onSubmitOne(event)}} >Logar</button>
     </form>
   </div> 
);
}

export default Login;
